import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

function AddEditBookPage() {
  const [book, setBook] = useState({ name: '', author: '', yearOfPublishing: '', isbn: '' });
  const navigate = useNavigate();
  const { id } = useParams(); // Use this for editing

  const handleChange = (e) => {
    const { name, value } = e.target;
    setBook(prevBook => ({ ...prevBook, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (id) {
        await axios.put(`${process.env.REACT_APP_API_URL}/books/${id}`, book);
      } else {
        await axios.post(`${process.env.REACT_APP_API_URL}/books`, book);
      }
      navigate('/'); 
    } catch (error) {
      console.error('There was an error saving the book', error);
      // Handle error here
    }
  };

  const deleteBook = async () => {
    if(window.confirm('Are you sure you want to delete this book?')) {
      try {
        await axios.delete(`${process.env.REACT_APP_API_URL}/books/${id}`);
        navigate('/'); // Redirect to the home page after deletion
      } catch (error) {
        console.error('There was an error deleting the book', error);
        // Handle error here
      }
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="name" value={book.name} onChange={handleChange} placeholder="Name" />
      <input name="author" value={book.author} onChange={handleChange} placeholder="Author" />
      <input name="yearOfPublishing" value={book.yearOfPublishing} onChange={handleChange} placeholder="Year of Publishing" />
      <input name="isbn" value={book.isbn} onChange={handleChange} placeholder="ISBN" />
      <button type="submit">Save</button>
      {id && <button type="button" onClick={deleteBook}>Delete</button>} {/* Show delete button only for existing books */}
      <button type="button" onClick={() => navigate.goBack()}>Cancel</button>
    </form>
  );
}

export default AddEditBookPage;
