import React from 'react';
import BookListItem from './BookListItem';
import axios from 'axios';

function BookList({ books }) {
  const navigate = useNavigate();

  const deleteBook = async (id) => {
    if(window.confirm('Are you sure you want to delete this book?')) {
      try {
        await axios.delete(`${process.env.REACT_APP_API_URL}/books/${id}`);
        // Code to refresh the list after deletion
        navigate(0); // Refresh the page, or use a more refined method to update the list
      } catch (error) {
        console.error('There was an error deleting the book', error);
      }
    }
  };

  return (
    <div>
      {books.map(book => (
        <BookListItem key={book.id} book={book} deleteBook={() => deleteBook(book.id)} />
      ))}
    </div>
  );
}


export default BookList;
